function loadFaqs(e=""){fetch(`../files/php/search.php?query=${encodeURIComponent(e)}`).then((e=>e.json())).then((e=>{const t=document.getElementById("faqList");t.innerHTML="",e.forEach(((e,n)=>{const a=`faq-${n}`,d=document.createElement("input");d.type="checkbox",d.id=a;const c=document.createElement("label");c.setAttribute("for",a);const o=document.createElement("p");o.classList.add("faq-heading"),o.textContent=e.question;const s=document.createElement("div");s.classList.add("faq-arrow");const l=document.createElement("p");l.classList.add("faq-text"),l.textContent=e.answer,c.appendChild(o),c.appendChild(s),c.appendChild(l),t.appendChild(d),t.appendChild(c)}))}))}function searchFaqs(){loadFaqs(document.getElementById("searchBar").value)}document.addEventListener("DOMContentLoaded",loadFaqs);